sleep 1;while true;do boot=$(getprop sys.boot_completed);if [ "$boot" = 1 ];then for a in $(getprop | grep init.svc | grep -E "thermal|Thermal|THERMAL" | cut -f 1 -d ] | cut -f 3 -d .); do stop $a;stop $a;done;for b in $(getprop | grep init.svc | grep -E "thermal|Thermal|THERMAL" | cut -f 1 -d ] | cut -f 4 -d .); do stop vendor.$b;stop vendor.$b;done;for c in $(getprop | grep -E "thermal|Thermal|THERMAL" | cut -f 2 -d [ | cut -f 1 -d ]); do resetprop -d $c;done;exit;fi;sleep 1;done